// Transformar los datos del tablero Kanban a formato CSV
const items = $input.all();
const data = items[0].json.data;
const email = items[0].json.email;

// Definir encabezados del CSV
const headers = ['ID', 'T铆tulo', 'Descripci贸n', 'Columna', 'Fecha de Creaci贸n'];

// Funci贸n para escapar campos CSV (manejar comillas y comas)
function escapeCSV(field) {
  if (field === null || field === undefined) {
    return '';
  }
  const stringField = String(field);
  // Si contiene comas, comillas o saltos de l铆nea, envolver en comillas
  if (stringField.includes(',') || stringField.includes('"') || stringField.includes('\n')) {
    return '"' + stringField.replace(/"/g, '""') + '"';
  }
  return stringField;
}

// Construir CSV
let csvContent = headers.join(',') + '\n';

data.forEach(item => {
  const row = [
    escapeCSV(item.id),
    escapeCSV(item.title),
    escapeCSV(item.description),
    escapeCSV(item.column),
    escapeCSV(new Date(item.createdAt).toLocaleString())
  ];
  csvContent += row.join(',') + '\n';
});

// Convertir a Base64 para adjuntar como archivo
const base64CSV = Buffer.from(csvContent).toString('base64');

return [
  {
    json: {
      email: email,
      csvContent: csvContent,
      base64CSV: base64CSV,
      totalCards: data.length,
      timestamp: new Date().toISOString()
    }
  }
];
